                        }]
                    },
                    options: {
                        responsive: true,
                        scales: {
                            y: {
                                beginAtZero: true
                            }
                        }
                    }
                });
            });
        // pie chart scrpit oreventive 
        // Initial Categorization
        fetch('/api/preventive-maintenance-initial-categorization')
            .then(response => response.json())
            .then(data => {
                const categories = Object.keys(data);
                const counts = Object.values(data);

