                new Chart(chartElement, {
                    type: 'pie',
                    data: {
                        labels: categories,
                        datasets: [{
                            label: 'Preventive Maintenance',
                            data: counts,
                            backgroundColor: ['#3498db', '#2ecc71', '#f39c12', '#e74c3c',
                                '#9b59b6'],
                            borderWidth: 1,
