          pointRadius: 5,  // Size of the points
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        aspectRatio: 1,
        scales: {
          x: {
            type: 'linear',
            position: 'bottom',
            title: {
              display: true,
              text: 'Statuses'
            }
          },
          y: {
            title: {
              display: true,
              text: 'Counts'
            }
          }
        }
      }
    });
  });


  }
</script>

